import React, { useState } from "react"

const NoteCalculator = () => {
  useEffect(() => {
    document.title = "Note Calculator" // Change the title when the component mounts
  }, [])
  const [notes, setNotes] = useState({
    20: 0,
    10: 0,
    5: 0,
    2: 0,
    1: 0,
    0.5: 0,
    0.2: 0,
    0.1: 0,
    0.05: 0,
  })
  const [total, setTotal] = useState(0)
  const [exceeds300, setExceeds300] = useState(false)
  const [notesToTakeOut, setNotesToTakeOut] = useState({})

  const calculateTotal = () => {
    let sum = 0
    for (const [note, count] of Object.entries(notes)) {
      sum += note * count
    }
    setTotal(sum)
    if (sum > 300) {
      setExceeds300(true)
      calculateNotesToTakeOut(sum - 300)
    } else {
      setExceeds300(false)
    }
  }

  const calculateNotesToTakeOut = (amount) => {
    const notesToTakeOut = {}
    const sortedNotes = Object.keys(notes).sort((a, b) => b - a)
    let remainingAmount = amount
    sortedNotes.forEach((note) => {
      const noteValue = parseFloat(note)
      const noteCount = Math.min(
        Math.floor(remainingAmount / noteValue),
        notes[note]
      )
      if (noteCount > 0) {
        notesToTakeOut[note] = noteCount
        remainingAmount -= noteCount * noteValue
      }
    })
    setNotesToTakeOut(notesToTakeOut)
  }

  const handleNoteChange = (value, note) => {
    const newNotes = { ...notes, [note]: value }
    setNotes(newNotes)
    calculateTotal()
  }

  return (
    <div>
      <h1>Note Calculator</h1>
      <div className="container">
        <div className="notes-container">
          {Object.entries(notes).map(([note, count]) => (
            <div className="notes" key={note}>
              <span>{`$${note}`}</span>
              <input
                type="number"
                value={count}
                onChange={(e) =>
                  handleNoteChange(parseInt(e.target.value), note)
                }
              />
            </div>
          ))}
        </div>
      </div>
      <div className="total">Total: ${total}</div>
      {exceeds300 && (
        <div className="container">
          <div className="note-to-take-out">
            <h2>Notes to take out to make total less than or equal to $300:</h2>
            {Object.entries(notesToTakeOut).map(([note, count]) => (
              <div key={note}>{`Take out ${count} of $${note}`}</div>
            ))}
          </div>
        </div>
      )}
    </div>
  )
}

export default NoteCalculator
